import React from './react'
import ReactDOM from './react-dom'

class Counter extends React.Component {
  constructor (props) {
    super(props);
  }

  render () {
    return (
      <div>
      Counter
      </div>
    )
  }
}

ReactDOM.render(
  <div className="a" style="color: red;">
  hello world
    <Counter />
  </div>,
  document.getElementById('root')
);
